import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import { Button, Modal, ModalBody, ModalFooter, ModalHeader, Table } from 'reactstrap';
import { v4 as uuidv4 } from 'uuid';
import '../assets/SCSS/CategoryComponent.scss';
import MyContext from '../contexts/MyContext';
import CategoryDetail from './CategoryDetailComponent';
import FooterComponent from './FooterComponent';

class Category extends Component {
  static contextType = MyContext;

  constructor(props) {
    super(props);
    this.state = {
      categories: [],
      itemSelected: null,
      isOpenModal: false,
    };
  }

  componentDidMount() {
    this.apiGetCategories();
  }

  toggleModal = () => {
    this.setState(prevState => ({
      isOpenModal: !prevState.isOpenModal
    }));
  }

  handleAddNewUser = () => {
    const newItemSelected = { _id: uuidv4(), name: '' };
    this.setState({
      itemSelected: newItemSelected,
      isOpenModal: true,
    });
  }

  trItemClick = (item) => {
    this.setState({ itemSelected: item, isOpenModal: true });
  }

  handleSaveCategory = (category) => {
    if (category._id) {
      this.apiUpdateCategory(category);
    } else {
      this.apiAddCategory(category);
    }
  }

  apiAddCategory = (category) => {
    axios.post('/api/admin/categories', category)
      .then(res => {
        alert('Category added successfully!');
        this.apiGetCategories();
        this.toggleModal();
      })
      .catch(error => {
        console.error('Error adding category:', error);
        alert('Failed to add category.');
      });
  }

  apiUpdateCategory = (category) => {
    axios.put(`/api/admin/categories/${category._id}`, category)
      .then(res => {
        alert('Category updated successfully!');
        this.apiGetCategories();
        this.toggleModal();
      })
      .catch(error => {
        console.error('Error updating category:', error);
        alert('Failed to update category.');
      });
  }

  apiGetCategories = () => {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.get('/api/admin/categories', config)
      .then(res => {
        const result = res.data;
        this.setState({ categories: result });
      })
      .catch(error => {
        console.error('Error fetching categories:', error);
      });
  }

  render() {
    const { categories, itemSelected, isOpenModal } = this.state;

    const cates = categories.map(category => (
      <tr key={category._id} className="datatable" onClick={() => this.trItemClick(category)}>
        <td>{category._id}</td>
        <td>{category.name}</td>
      </tr>
    ));

    return (
      <div>
        <div className="float-left">
          <Button color="primary" onClick={this.handleAddNewUser}>Add New Category</Button>
          <Table className="table table-bordered">
            <thead className="thead-dark">
              <tr>
                <th className="table-dark">ID</th>
                <th className="table-dark">Name</th>
              </tr>
            </thead>
            <tbody>
              {cates}
            </tbody>
          </Table>
        </div>

        <Modal isOpen={isOpenModal} toggle={this.toggleModal}>
          <ModalHeader toggle={this.toggleModal}>{itemSelected && itemSelected._id ? 'Edit Category' : 'Add New Category'}</ModalHeader>
          <ModalBody>
            <CategoryDetail item={itemSelected} onSave={this.handleSaveCategory} />
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.toggleModal}>Cancel</Button>
          </ModalFooter>
        </Modal>

        <FooterComponent />
      </div>
    );
  }
}

export default Category;
