import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import MyContext from '../contexts/MyContext';

class CategoryDetail extends Component {
  static contextType = MyContext;

  constructor(props) {
    super(props);
    this.state = {
      txtID: '',    // Trạng thái để lưu ID
      txtName: ''   // Trạng thái để lưu Tên
    };
  }

  componentDidUpdate(prevProps) {
    // Cập nhật state khi props thay đổi (thay đổi item)
    if (this.props.item !== prevProps.item) {
      this.setState({ 
        txtID: this.props.item ? this.props.item._id : '', 
        txtName: this.props.item ? this.props.item.name : '' 
      });
    }
  }

  handleInputChange = (e) => {
    // Xử lý sự kiện thay đổi cho trường Tên
    this.setState({ [e.target.name]: e.target.value });
  }

  btnAddClick = (e) => {
    e.preventDefault();
    const { txtName } = this.state;
    if (txtName) {
      const category = { name: txtName };
      this.apiPostCategory(category);
    } else {
      alert('Vui lòng nhập tên danh mục');
    }
  }

  btnUpdateClick = (e) => {
    e.preventDefault();
    const { txtID, txtName } = this.state;
    if (txtID && txtName) {
      const category = { name: txtName };
      this.apiPutCategory(txtID, category);
    } else {
      alert('Vui lòng nhập ID và tên danh mục');
    }
  }

  // Gọi API để thêm mới danh mục
  apiPostCategory = (category) => {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.post('/api/admin/categories', category, config)
      .then((res) => {
        const result = res.data;
        if (result) {
          alert('Thêm danh mục thành công!');
          this.apiGetCategories();
        } else {
          alert('Thêm danh mục thất bại.');
        }
      })
      .catch((error) => {
        console.error('Lỗi khi thêm danh mục:', error);
        alert('Thêm danh mục thất bại.');
      });
  }

  // Gọi API để cập nhật danh mục
  apiPutCategory = (id, category) => {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.put(`/api/admin/categories/${id}`, category, config)
      .then((res) => {
        const result = res.data;
        if (result) {
          alert('Cập nhật danh mục thành công!');
          this.apiGetCategories();
        } else {
          alert('Cập nhật danh mục thất bại.');
        }
      })
      .catch((error) => {
        console.error('Lỗi khi cập nhật danh mục:', error);
        alert('Cập nhật danh mục thất bại.');
      });
  }

  // Gọi API để lấy danh sách danh mục
  apiGetCategories = () => {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.get('/api/admin/categories', config)
      .then((res) => {
        const result = res.data;
        this.props.updateCategories(result);
      })
      .catch((error) => {
        console.error('Lỗi khi lấy danh sách danh mục:', error);
      });
  }

  render() {
    return (
      <div className="float-right">
        <h2 className="text-center">CHI TIẾT DANH MỤC</h2>
        <form>
          <table className="table">
            <tbody>
              <tr>
                <td>ID</td>
                <td>
                  <div className="input-group mb-3">
                    <input
                      type="text"
                      className="form-control"
                      value={this.state.txtID}
                      onChange={this.handleInputChange}
                      readOnly={true}
                      name="txtID"
                    />
                  </div>
                </td>
              </tr>
              <tr>
                <td>Tên</td>
                <td>
                  <div className="input-group mb-3">
                    <input
                      type="text"
                      className="form-control"
                      value={this.state.txtName}
                      onChange={this.handleInputChange}
                      name="txtName"
                    />
                  </div>
                </td>
              </tr>
              <tr>
                <td></td>
                <td>
                  <div className="d-flex gap-2">
                    <input
                      type="submit"
                      className="btn btn-primary"
                      value="THÊM MỚI"
                      onClick={this.btnAddClick}
                    />
                    <input
                      type="submit"
                      className="btn btn-warning"
                      value="CẬP NHẬT"
                      onClick={this.btnUpdateClick}
                    />
                    <input
                      type="submit"
                      className="btn btn-danger"
                      value="XÓA"
                    />
                  </div>
                </td>
              </tr>
            </tbody>
          </table>
        </form>
      </div>
    );
  }
}

export default CategoryDetail;
