import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import MyContext from '../contexts/MyContext';
import './Customer.scss'; // Import SCSS file

class Customer extends Component {
  static contextType = MyContext; // using this.context to access global state

  constructor(props) {
    super(props);
    this.state = {
      customers: [],
      orders: [],
      order: null
    };
  }

  async componentDidMount() {
    await this.apiGetCustomers();
  }

  render() {
    const customers = this.state.customers.map((item) => (
      <tr key={item._id} className="datatable" onClick={() => this.trCustomerClick(item)}>
        <td>{item._id}</td>
        <td>{item.username}</td>
        <td>{item.password}</td>
        <td>{item.name}</td>
        <td>{item.phone}</td>
        <td>{item.email}</td>
        <td>{item.active}</td>
        <td>
          {item.active === 0 ?
            <span className="link" onClick={() => this.lnkEmailClick(item)}>EMAIL</span>
            :
            <span className="link" onClick={() => this.lnkDeactiveClick(item)}>DEACTIVE</span>}
        </td>
      </tr>
    ));

    const orders = this.state.orders.map((item) => (
      <tr key={item._id} className="datatable" onClick={() => this.trOrderClick(item)}>
        <td>{item._id}</td>
        <td>{new Date(item.cdate).toLocaleString()}</td>
        <td>{item.customer.name}</td>
        <td>{item.customer.phone}</td>
        <td>{item.total}</td>
        <td>{item.status}</td>
      </tr>
    ));

    const items = this.state.order ? this.state.order.items.map((item, index) => (
      <tr key={item.product._id} className="datatable">
        <td>{index + 1}</td>
        <td>{item.product._id}</td>
        <td>{item.product.name}</td>
        <td><img src={"data:image/jpg;base64," + item.product.image} width="70px" height="70px" alt="" /></td>
        <td>{item.product.price}</td>
        <td>{item.quantity}</td>
        <td>{item.product.price * item.quantity}</td>
      </tr>
    )) : null;

    return (
      <div className="customer">
        <div className="align-center">
          <h2 className="text-center">CUSTOMER LIST</h2>
          <table className="table table-bordered" border="1">
            <tbody>
              <tr className="datatable">
                <th className="table-dark">ID</th>
                <th className="table-dark">Username</th>
                <th className="table-dark">Password</th>
                <th className="table-dark">Name</th>
                <th className="table-dark">Phone</th>
                <th className="table-dark">Email</th>
                <th className="table-dark">Active</th>
                <th className="table-dark">Action</th>
              </tr>
              {customers}
            </tbody>
          </table>
        </div>

        {this.state.orders.length > 0 &&
          <div className="align-center">
            <h2 className="text-center">ORDER LIST</h2>
            <table className="table table-bordered" border="1">
              <tbody>
                <tr className="datatable">
                  <th className="table-dark">ID</th>
                  <th className="table-dark">Creation date</th>
                  <th className="table-dark">Cust.name</th>
                  <th className="table-dark">Cust.phone</th>
                  <th className="table-dark">Total</th>
                  <th className="table-dark">Status</th>
                </tr>
                {orders}
              </tbody>
            </table>
          </div>
        }

        {this.state.order &&
          <div className="align-center">
            <h2 className="text-center">ORDER DETAIL</h2>
            <table className="table table-bordered" border="1">
              <tbody>
                <tr className="datatable">
                  <th className="table-dark">No.</th>
                  <th className="table-dark">Prod.ID</th>
                  <th className="table-dark">Prod.name</th>
                  <th className="table-dark">Image</th>
                  <th className="table-dark">Price</th>
                  <th className="table-dark">Quantity</th>
                  <th className="table-dark">Amount</th>
                </tr>
                {items}
              </tbody>
            </table>
          </div>
        }
      </div>
    );
  }

  async apiGetCustomers() {
    try {
      const config = { headers: { 'x-access-token': this.context.token } };
      const res = await axios.get('/api/admin/customers', config);
      this.setState({ customers: res.data });
    } catch (error) {
      console.error("Error fetching customers:", error);
    }
  }

  async apiGetOrdersByCustID(cid) {
    try {
      const config = { headers: { 'x-access-token': this.context.token } };
      const res = await axios.get('/api/admin/orders/customer/' + cid, config);
      this.setState({ orders: res.data });
    } catch (error) {
      console.error("Error fetching orders:", error);
    }
  }

  async apiPutCustomerDeactive(id, token) {
    try {
      const body = { token: token };
      const config = { headers: { 'x-access-token': this.context.token } };
      const res = await axios.put('/api/admin/customers/deactive/' + id, body, config);
      if (res.data) {
        await this.apiGetCustomers();
      } else {
        alert('SORRY BABY!');
      }
    } catch (error) {
      console.error("Error deactivating customer:", error);
    }
  }

  async apiGetCustomerSendmail(id) {
    try {
      const config = { headers: { 'x-access-token': this.context.token } };
      const res = await axios.get('/api/admin/customers/sendmail/' + id, config);
      alert(res.data.message);
    } catch (error) {
      console.error("Error sending email:", error);
    }
  }

  trCustomerClick(item) {
    this.setState({ orders: [], order: null });
    this.apiGetOrdersByCustID(item._id);
  }

  trOrderClick(item) {
    this.setState({ order: item });
  }

  lnkDeactiveClick(item) {
    this.apiPutCustomerDeactive(item._id, item.token);
  }

  lnkEmailClick(item) {
    this.apiGetCustomerSendmail(item._id);
  }
}

export default Customer;
