import axios from 'axios';
import React, { Component } from 'react';
import MyContext from '../contexts/MyContext';

class CategoryDetail extends Component {
  static contextType = MyContext;

  constructor(props) {
    super(props);
    this.state = {
      txtID: '',
      txtName: ''
    };
  }

  componentDidUpdate(prevProps) {
    if (this.props.item !== prevProps.item) {
      const { _id, name } = this.props.item;
      this.setState({ txtID: _id, txtName: name });
    }
  }

  render() {
    return (
      <div className="float-right">
        <h2 className="text-center">{this.props.item ? 'Edit Category' : 'Add New Category'}</h2>
        <form>
          <table className="table">
            <tbody>
              <tr>
                <td>ID</td>
                <td>
                  <div className="input-group mb-3">
                    <input
                      type="text"
                      className="form-control"
                      value={this.state.txtID}
                      onChange={(e) => { this.setState({ txtID: e.target.value }) }}
                      readOnly={true}
                    />
                  </div>
                </td>
              </tr>
              <tr>
                <td>Name</td>
                <td>
                  <div className="input-group mb-3">
                    <input
                      type="text"
                      className="form-control"
                      value={this.state.txtName}
                      onChange={(e) => { this.setState({ txtName: e.target.value }) }}
                    />
                  </div>
                </td>
              </tr>
              <tr>
                <td></td>
                <td>
                  <div className="d-flex gap-2">
                    <input
                      type="submit"
                      className="btn btn-primary"
                      value="ADD NEW"
                      onClick={(e) => this.btnAddClick(e)}
                    />
                    <input
                      type="submit"
                      className="btn btn-warning"
                      value="UPDATE"
                      onClick={(e) => this.btnUpdateClick(e)}
                    />
                    <input
                      type="submit"
                      className="btn btn-danger"
                      value="DELETE"
                    />
                  </div>
                </td>
              </tr>
            </tbody>
          </table>
        </form>
      </div>
    );
  }

  // event-handlers
  btnAddClick = (e) => {
    e.preventDefault();
    const name = this.state.txtName;
    if (name) {
      const cate = { name: name };
      this.apiPostCategory(cate);
    } else {
      alert('Please input name');
    }
  }

  btnUpdateClick = (e) => {
    e.preventDefault();
    const id = this.state.txtID;
    const name = this.state.txtName;
    if (id && name) {
      const cate = { name: name };
      this.apiPutCategory(id, cate);
    } else {
      alert('Please input id and name');
    }
  }

  // APIs
  apiPostCategory = (cate) => {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.post('/api/admin/categories', cate, config)
      .then(res => {
        const result = res.data;
        if (result) {
          alert('Category added successfully!');
          this.props.onSave(result);
        } else {
          alert('Failed to add category.');
        }
      })
      .catch(error => {
        console.error('Error adding category:', error);
        alert('Failed to add category.');
      });
  }

  apiPutCategory = (id, cate) => {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.put(`/api/admin/categories/${id}`, cate, config)
      .then(res => {
        const result = res.data;
        if (result) {
          alert('Category updated successfully!');
          this.props.onSave(result);
        } else {
          alert('Failed to update category.');
        }
      })
      .catch(error => {
        console.error('Error updating category:', error);
        alert('Failed to update category.');
      });
  }
}

export default CategoryDetail;
