import { faPlus } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import "../assets/SCSS/CategoryComponent.scss";
import MyContext from '../contexts/MyContext';
import CategoryDetail from './CategoryDetailComponent';
import FooterComponent from './FooterComponent';
class Category extends Component {
  static contextType = MyContext; // using this.context to access global state
  constructor(props) {
    super(props);
    this.state = {
      categories: [],
      itemSelected: null
    };
  }
  handleAddNewUser = () => {
    this.setState({
        isOpenModal: true,
    });
}
  render() {
    const pagination = Array.from({ length: this.state.noPages }, (_, index) => {
      if ((index + 1) === this.state.curPage) {
          return (<span key={index}>| <b>{index + 1}</b> |</span>);
      } else {
          return (<span key={index} className="link" onClick={() => this.lnkPageClick(index + 1)}>| {index + 1} |</span>);
      }
  });
    const cates = this.state.categories.map((item) => {
      return (
        <tr key={item._id} className="datatable" onClick={() => this.trItemClick(item)}>
          <td>{item._id}</td>
          <td>{item.name}</td>
        </tr>
      );
    });
    return (
      <div>
        <div className="float-left">
          {/* <h2 className="text-center">CATEGORY LIST</h2> */}
          <table className="table table-bordered" >
            <tbody>
            <button 
                        className='btn btn-primary px-3'
                        onClick={this.handleAddNewUser}>
                        <FontAwesomeIcon icon={faPlus} /> Add new category
                    </button>
              <tr className="thead-dark">
                <th class="table-dark">ID</th>
                <th class="table-dark">Name</th>
              </tr>
              {cates}
            </tbody>
          </table>
        </div>
        <div className="inline" />
        <CategoryDetail item={this.state.itemSelected} updateCategories={this.updateCategories} />
        <div className="float-clear" />
        <FooterComponent /> 
      </div>
    );
  }
  updateCategories = (categories) => { // arrow-function
    this.setState({ categories: categories });
  }
  componentDidMount() {
    this.apiGetCategories();
  }
  // event-handlers
  trItemClick(item) {
    this.setState({ itemSelected: item });
  }
  // apis
  apiGetCategories() {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.get('/api/admin/categories', config).then((res) => {
      const result = res.data;
      this.setState({ categories: result });
    });
  }
}
export default Category;
