// Product.js

import axios from 'axios';
import React, { Component } from 'react';
import MyContext from '../contexts/MyContext';
import FooterComponent from './FooterComponent';
import ProductComponent from './ProductComponent';

class Product extends Component {
  static contextType = MyContext; // using this.context to access global state

  constructor(props) {
    super(props);
    this.state = {
      products: [],
      noPages: 0,
      curPage: 1,
      itemSelected: null,
      email: ''
    };
  }

  render() {
    const { products, curPage } = this.state;

    const prods = products.map((item) => (
      <tr key={item._id} className="datatable" onClick={() => this.trItemClick(item)}>
        <td>{item._id}</td>
        <td>{item.name}</td>
        <td>{item.price}</td>
        <td>{new Date(item.cdate).toLocaleString()}</td>
        <td>{item.category.name}</td>
        <td><img src={`data:image/jpg;base64,${item.image}`} width="100px" height="100px" alt="" /></td>
      </tr>
    ));

    const pagination = Array.from({ length: this.state.noPages }, (_, index) => (
      (index + 1) === this.state.curPage ?
        <span key={index}>| <b>{index + 1}</b> |</span> :
        <span key={index} className="link" onClick={() => this.lnkPageClick(index + 1)}>| {index + 1} |</span>
    ));

    return (
      <div>
        <div className="float-left">
          <h2 className="text-center">PRODUCT LIST</h2>
          <table className="table table-bordered product-component ">
            <thead className="thead-dark">
              <tr>
                <th className="table-dark">ID</th>
                <th className="table-dark">Name</th>
                <th className="table-dark">Price</th>
                <th className="table-dark">Creation date</th>
                <th className="table-dark">Category</th>
                <th className="table-dark">Image</th>
              </tr>
            </thead>
            <tbody>
              {prods}
              <tr>
                <td colSpan="6">{pagination}</td>
              </tr>
            </tbody>
          </table>
        </div>

        <div className="inline" />

        <ProductComponent item={this.state.itemSelected} curPage={curPage} updateProducts={this.updateProducts} />

        <div className="float-clear" />

        <FooterComponent /> 
      </div>
    );
  }

  updateProducts = (products, noPages, curPage) => {
    this.setState({ products, noPages, curPage });
  }

  componentDidMount() {
    this.apiGetProducts(this.state.curPage);
  }

  lnkPageClick = (index) => {
    this.apiGetProducts(index);
  }

  trItemClick = (item) => {
    this.setState({ itemSelected: item });
  }

  handleSubmit = () => {
    // Implement your email subscription logic here
    console.log('Email submitted:', this.state.email);
  }

  apiGetProducts = (page) => {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.get(`/api/admin/products?page=${page}`, config).then((res) => {
      const { products, noPages, curPage } = res.data;
      this.setState({ products, noPages, curPage });
    }).catch((error) => {
      console.error('Error fetching products:', error);
    });
  }
}

export default Product;
