import React, { Component } from 'react';
import { Button, Table } from 'reactstrap';
import axios from 'axios';
import CategoryDetail from './CategoryDetail'; // Component để hiển thị modal thêm mới/chỉnh sửa

class Category extends Component {
  constructor(props) {
    super(props);
    this.state = {
      categories: [],         // Danh sách danh mục
      itemSelected: null,     // Đối tượng danh mục đang được chọn để chỉnh sửa
      isOpenModal: false      // Trạng thái hiển thị modal
    };
  }

  // Gọi API để lấy danh sách danh mục khi component được mount
  componentDidMount() {
    this.apiGetCategories();
  }

  // Gọi API lấy danh sách danh mục
  apiGetCategories = () => {
    axios.get('/api/admin/categories')
      .then(res => {
        const categories = res.data;
        this.setState({ categories });
      })
      .catch(error => {
        console.error('Error fetching categories:', error);
      });
  }

  // Xử lý khi click vào một hàng trong bảng danh sách danh mục
  trItemClick = (item) => {
    this.setState({ itemSelected: item });
  }

  // Xử lý khi thêm mới hoặc cập nhật danh mục
  handleSaveCategory = (category) => {
    // Nếu có _id tức là đang update, ngược lại là thêm mới
    if (category._id) {
      this.apiUpdateCategory(category);
    } else {
      this.apiAddCategory(category);
    }
  }

  // Gọi API để thêm mới danh mục
  apiAddCategory = (category) => {
    axios.post('/api/admin/categories', category)
      .then(res => {
        alert('Category added successfully!');
        this.apiGetCategories();  // Sau khi thêm thành công, load lại danh sách
        this.toggleModal();       // Đóng modal sau khi thêm thành công
      })
      .catch(error => {
        console.error('Error adding category:', error);
        alert('Failed to add category.');
      });
  }

  // Gọi API để cập nhật danh mục
  apiUpdateCategory = (category) => {
    axios.put(`/api/admin/categories/${category._id}`, category)
      .then(res => {
        alert('Category updated successfully!');
        this.apiGetCategories();  // Sau khi cập nhật thành công, load lại danh sách
        this.toggleModal();       // Đóng modal sau khi cập nhật thành công
      })
      .catch(error => {
        console.error('Error updating category:', error);
        alert('Failed to update category.');
      });
  }

  // Toggle modal thêm mới/chỉnh sửa
  toggleModal = () => {
    this.setState(prevState => ({
      isOpenModal: !prevState.isOpenModal,
      itemSelected: null  // Reset itemSelected khi đóng modal
    }));
  }

  render() {
    const { categories, itemSelected, isOpenModal } = this.state;

    const categoryRows = categories.map(category => (
      <tr key={category._id} className="datatable" onClick={() => this.trItemClick(category)}>
        <td>{category._id}</td>
        <td>{category.name}</td>
      </tr>
    ));

    return (
      <div>
        <div className="float-left">
          <Button className="btn btn-primary px-3" onClick={this.toggleModal}>
            Add new category
          </Button>
          <Table className="table table-bordered">
            <thead className="thead-dark">
              <tr>
                <th className="table-dark">ID</th>
                <th className="table-dark">Name</th>
              </tr>
            </thead>
            <tbody>
              {categoryRows}
            </tbody>
          </Table>
        </div>

        {/* Modal thêm mới/chỉnh sửa danh mục */}
        <CategoryDetail
          category={itemSelected}
          isOpen={isOpenModal}
          toggle={this.toggleModal}
          onSave={this.handleSaveCategory}
        />

        {/* Component footer */}
        {/* <FooterComponent /> */}
      </div>
    );
  }
}

export default Category;
