import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import { Button, Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';
import "../assets/SCSS/CategoryDetailComponent.scss";
import MyContext from '../contexts/MyContext';

class CategoryDetail extends Component {
  static contextType = MyContext; // using this.context to access global state

  constructor(props) {
    super(props);
    this.state = {
      txtID: '',
      txtName: ''
    };
  }

  // Function to handle adding new category
  handleAddNewCategory = () => {
    // Implement your logic here to add a new category
    // Example: you can call this.apiPostCategory(cate);
    // and handle the response accordingly
    alert('Add new category logic goes here');
  };

  // Function to handle updating category
  handleUpdateCategory = () => {
    // Implement your logic here to update a category
    // Example: you can call this.apiPutCategory(id, cate);
    // and handle the response accordingly
    alert('Update category logic goes here');
  };

  render() {
    return (
      <Modal isOpen={this.props.isOpen} toggle={this.props.toggle} className="custom-modal-header" size="lg">
        <ModalHeader toggle={this.props.toggle}>CATEGORY DETAIL</ModalHeader>
        <ModalBody>
          <div className='modal-user-body'>
            <div className='input-container'>
              <label>ID</label>
              <input
                type="text"
                className="form-control"
                value={this.state.txtID}
                onChange={(e) => { this.setState({ txtID: e.target.value }) }}
                readOnly={true}
              />
            </div>
            <div className='input-container'>
              <label>Name</label>
              <input
                type="text"
                className="form-control"
                value={this.state.txtName}
                onChange={(e) => { this.setState({ txtName: e.target.value }) }}
              />
            </div>
          </div>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" className='px-3' onClick={this.handleAddNewCategory}>Add New</Button>{' '}
          <Button color="warning" className='px-3' onClick={this.handleUpdateCategory}>Update</Button>{' '}
          <Button color="danger" className='px-3' onClick={this.props.toggle}>Cancel</Button>
        </ModalFooter>
      </Modal>
    );
  }

  componentDidUpdate(prevProps) {
    if (this.props.item !== prevProps.item) {
      this.setState({ txtID: this.props.item._id, txtName: this.props.item.name });
    }
  }

  // APIs
  apiPostCategory(cate) {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.post('/api/admin/categories', cate, config).then((res) => {
      const result = res.data;
      if (result) {
        alert('Category added successfully!');
        this.apiGetCategories(); // Update categories after adding
      } else {
        alert('Failed to add category.');
      }
    }).catch(error => {
      console.error('Error adding category:', error);
      alert('An error occurred while adding category.');
    });
  }

  apiPutCategory(id, cate) {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.put(`/api/admin/categories/${id}`, cate, config).then((res) => {
      const result = res.data;
      if (result) {
        alert('Category updated successfully!');
        this.apiGetCategories(); // Update categories after updating
      } else {
        alert('Failed to update category.');
      }
    }).catch(error => {
      console.error('Error updating category:', error);
      alert('An error occurred while updating category.');
    });
  }

  apiGetCategories() {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.get('/api/admin/categories', config).then((res) => {
      const result = res.data;
      this.props.updateCategories(result); // Update parent component's categories state
    }).catch(error => {
      console.error('Error fetching categories:', error);
      alert('An error occurred while fetching categories.');
    });
  }
}

export default CategoryDetail;
