import { faPlus } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import axios from 'axios';
import React, { Component } from 'react';
import { Button, Table } from 'reactstrap';
import '../assets/SCSS/CategoryComponent.scss';
import MyContext from '../contexts/MyContext';
import CategoryDetail from './CategoryDetailComponent';
import FooterComponent from './FooterComponent';

class Category extends Component {
  static contextType = MyContext;

  constructor(props) {
    super(props);
    this.state = {
      categories: [],
      itemSelected: null,
      isOpenModal: false, // State to control modal visibility
    };
  }

  // Toggle modal visibility
  toggle = () => {
    this.setState(prevState => ({
      isOpenModal: !prevState.isOpenModal
    }));
  }
  toggleModal = () => {
    this.setState({
        isOpenModal: !this.state.isOpenModal,
    });
}

  // Handle click on Add new category button
  handleAddNewUser = () => {
    this.setState({
      isOpenModal: true,
    });
  }

  render() {
    const { categories, itemSelected, isOpenModal } = this.state;

    const cates = categories.map(item => (
      <tr key={item._id} className="datatable" onClick={() => this.trItemClick(item)}>
        <td>{item._id}</td>
        <td>{item.name}</td>
      </tr>
    ));

    return (
      <div>
        <div className="float-left">
          <Button className="btn btn-primary px-3" onClick={this.handleAddNewUser}>
            <FontAwesomeIcon icon={faPlus} /> Add new category
          </Button>
          <Table className="table table-bordered">
            <thead className="thead-dark">
              <tr>
                <th className="table-dark">ID</th>
                <th className="table-dark">Name</th>
              </tr>
            </thead>
            <tbody>
              {cates}
            </tbody>
          </Table>
        </div>

        <CategoryDetail item={itemSelected} updateCategories={this.updateCategories} isOpen={isOpenModal} toggle={this.toggle} />
        <FooterComponent />
      </div>
    );
  }

  componentDidMount() {
    this.apiGetCategories();
  }

  // Event handler when clicking on a row
  trItemClick(item) {
    this.setState({ itemSelected: item });
  }

  // Update categories state
  updateCategories = (categories) => {
    this.setState({ categories });
  }

  // Fetch categories from API
  apiGetCategories() {
    const config = { headers: { 'x-access-token': this.context.token } };
    axios.get('/api/admin/categories', config)
      .then(res => {
        const result = res.data;
        this.setState({ categories: result });
      })
      .catch(error => {
        console.error('Error fetching categories:', error);
      });
  }
}

export default Category;
