import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import MyContext from '../contexts/MyContext';

class CategoryDetail extends Component {
  static contextType = MyContext;

  constructor(props) {
    super(props);
    this.state = {
      txtID: '',
      txtName: ''
    };
  }

  componentDidMount() {
    if (this.props.item) {
      this.setState({
        txtID: this.props.item._id,
        txtName: this.props.item.name
      });
    }
  }

  componentDidUpdate(prevProps) {
    if (this.props.item !== prevProps.item) {
      this.setState({
        txtID: this.props.item ? this.props.item._id : '',
        txtName: this.props.item ? this.props.item.name : ''
      });
    }
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const { txtID, txtName } = this.state;
    const category = { _id: txtID, name: txtName };
    this.props.onSave(category);
  }

  render() {
    return (
      <div className="float-right">
        <h2 className="text-center">CATEGORY DETAIL</h2>
        <form onSubmit={this.handleSubmit}>
          <table className="table">
            <tbody>
              <tr>
                <td>ID</td>
                <td>
                  <div className="input-group mb-3">
                    <input
                      type="text"
                      className="form-control"
                      name="txtID"
                      value={this.state.txtID}
                      onChange={this.handleChange}
                      readOnly
                    />
                  </div>
                </td>
              </tr>
              <tr>
                <td>Name</td>
                <td>
                  <div className="input-group mb-3">
                    <input
                      type="text"
                      className="form-control"
                      name="txtName"
                      value={this.state.txtName}
                      onChange={this.handleChange}
                    />
                  </div>
                </td>
              </tr>
              <tr>
                <td></td>
                <td>
                  <div className="d-flex gap-2">
                    <input
                      type="submit"
                      className="btn btn-primary"
                      value={this.props.item && this.props.item._id ? 'UPDATE' : 'ADD NEW'}
                    />
                  </div>
                </td>
              </tr>
            </tbody>
          </table>
        </form>
      </div>
    );
  }
}

export default CategoryDetail;
